import React, { Component } from "react";
import { StyleSheet, Text, View, Button, ScrollView } from "react-native";

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  header: {
    height: 60,
    backgroundColor: "#000",
  },
  title: {
    color: "#fff",
    fontSize: 20,
    fontWeight: "bold",
  },
  menuButton: {
    height: 40,
    width: 40,
    borderRadius: 20,
    backgroundColor: "#fff",
    justifyContent: "center",
    alignItems: "center",
  },
  menuIcon: {
    color: "#000",
    fontSize: 20,
  },
  menuList: {
    width: 200,
    position: "absolute",
    top: 60,
    left: 0,
    zIndex: 100,
    backgroundColor: "#fff",
    borderRadius: 10,
    boxShadow: "0 0 10px rgba(0, 0, 0, 0.5)",
    padding: 20,
  },
  menuItem: {
    fontSize: 16,
    fontWeight: "bold",
    color: "#000",
    padding: 10,
  },
  homepage: {
    width: "100%",
    height: "100%",
  },
});

class App extends Component {
  state = {
    menuOpen: false,
  };

  handleMenuClick = () => {
    this.setState({
      menuOpen: !this.state.menuOpen,
    });
  };

  render() {
    const { menuOpen } = this.state;

    return (
      <View style={styles.container}>
        <View style={styles.header}>
          <Text style={styles.title}>Motel Website</Text>
          <Button
            title="Menu"
            style={styles.menuButton}
            onPress={this.handleMenuClick}
          />
        </View>
        <ScrollView>
          {menuOpen ? (
            <View style={styles.menuList}>
              <Button title="Home" />
              <Button title="Rooms" />
              <Button title="Photo Gallery" />
              <Button title="Location & Directions" />
              <Button title="Near & Around Us" />
              <Button title="Contact Us" />
            </View>
          ) : null}
          <View style={styles.homepage}>This is the homepage.</View>
        </ScrollView>
      </View>
    );
  }
}

export default App;
